INSERT INTO [dbo].[sys_wap_book] ([id], [userid], [username], [book_classid], [book_title], [book_author], [book_pub], [book_content], [book_date], [HangBiaoShi]) VALUES (N'3230', NULL, NULL, N'99', N'.满足性心灵 给她真正的高潮', N'不详', N'不详', N'G点高潮满足了，丈夫也射精了，此时双方都感到松弛疲倦，可是别就此打住——还有另一种高潮的境界：“阴核”高潮的祝福等着你强打起精神献给妻子作礼物。 
