INSERT INTO [dbo].[sys_wap_book] ([id], [userid], [username], [book_classid], [book_title], [book_author], [book_pub], [book_content], [book_date], [HangBiaoShi]) VALUES (N'3232', NULL, NULL, N'99', N'巧妙用椅子性爱也疯狂', N'不详', N'不详', N'椅子不仅可用来休憩，更可给你一次满意放松的性爱。对于那些每天疲于应付工作导致身体疲乏的男士，或者喜欢尝试新鲜刺激的性爱的人，这是一个不错的方式。不过，最好是选择一张舒适一点的椅子哦！ 
