INSERT INTO [dbo].[sys_wap_book] ([id], [userid], [username], [book_classid], [book_title], [book_author], [book_pub], [book_content], [book_date], [HangBiaoShi]) VALUES (N'3279', NULL, NULL, N'100', N'做爱时放声大叫', N'不详', N'不详', N'英国的专家提出减压新法，就是放声大叫，不单有益身心，还可以增强性欲。根据英国<<卫报>>报道，压力治疗师刘易斯进行的研究指出，人们在大叫后会有更强烈的性欲望，还会令他们更开放、更冲劲和兴奋。“结果显示大叫对家庭和工作均有正面影响。”
