INSERT INTO [dbo].[sys_wap_book] ([id], [userid], [username], [book_classid], [book_title], [book_author], [book_pub], [book_content], [book_date], [HangBiaoShi]) VALUES (N'3285', NULL, NULL, N'100', N'男人的性感区', N'不详', N'不详', N'男子性感区是指男子身体某些部位具有特殊的敏感性，适当地刺激这些部位，可以引起性兴奋。男性性感区有：
