INSERT INTO [dbo].[sys_wap_book] ([id], [userid], [username], [book_classid], [book_title], [book_author], [book_pub], [book_content], [book_date], [HangBiaoShi]) VALUES (N'3292', NULL, NULL, N'100', N'她的圣泉就是你们的润滑剂...', N'不详', N'不详', N'在轴与轴套间添加的润滑油一般，当两性交接之际，女性的阴道中自然而然分泌出的液体，俗称：“爱液”。对它有一种很有诗意的说法：“爱液是男性眼中的圣泉”。
