INSERT INTO [dbo].[sys_wap_book] ([id], [userid], [username], [book_classid], [book_title], [book_author], [book_pub], [book_content], [book_date], [HangBiaoShi]) VALUES (N'3300', NULL, NULL, N'101', N'男人性爱热度有几分', N'不详', N'不详', N'在心理治疗中心担任客串医生期间接受过不少已婚男人的咨询。他们的一个共同焦虑是：为什么对性爱不再像度蜜月时那么富有激情，有时甚至感到索然寡味，像完成某种任务一样。也有不少女性诉苦说，自己丈夫有点“反常”，“好景不再”。 
