INSERT INTO [dbo].[sys_wap_book] ([id], [userid], [username], [book_classid], [book_title], [book_author], [book_pub], [book_content], [book_date], [HangBiaoShi]) VALUES (N'3301', NULL, NULL, N'101', N'九位女性谈自慰时的感受', N'不详', N'不详', N'有时做爱和我自己——关于自慰的女性体验 
