INSERT INTO [dbo].[sys_wap_book] ([id], [userid], [username], [book_classid], [book_title], [book_author], [book_pub], [book_content], [book_date], [HangBiaoShi]) VALUES (N'3302', NULL, NULL, N'101', N'几个极富感官诱惑的动作', N'不详', N'不详', N'你要不要试试看，这几个动作都是极富感官诱惑的动作哦。深情地注视，急促地呼吸，轻轻地牵手，无声地靠近……这些看似微小的小动作，却能在瞬间转化成发自肺腑的性呼唤，让两个相爱的人擦出耀眼的性爱火花……
