INSERT INTO [dbo].[sys_wap_book] ([id], [userid], [username], [book_classid], [book_title], [book_author], [book_pub], [book_content], [book_date], [HangBiaoShi]) VALUES (N'3304', NULL, NULL, N'101', N'揭秘女人们的性幻想内幕', N'不详', N'不详', N'毫无疑问，年轻女性的性幻想是所有性活动中的重要组成部分。今日在马路上流连，被问：“要生活片吗？”我一时过于错愕，“你要卖我A片？” 登时吓倒路边的各色男人一片。其实，我被如此惊吓以至口不择言，还是有原因的——因为我是女人，超级会幻想的女人啊！ 
