INSERT INTO [dbo].[sys_wap_book] ([id], [userid], [username], [book_classid], [book_title], [book_author], [book_pub], [book_content], [book_date], [HangBiaoShi]) VALUES (N'3310', NULL, NULL, N'97', N'安装完美字库后MP3歌名仍为乱码的解决方法', N'不详', N'不详', N'本人用的是MP3PLAY，一直以来都是乱码，也习惯了。后来得到大家的帮助，下载了刀疤先生的QD最完美字体，安装后发现我的MP3PLAY菜单到是汉化了，待机画面的时间显示字体也变大了，可是里面的歌曲名还是一团乱码。晕………… 经过一番研究，终于解决了问题。如下：我从我电脑里面的MP3库里面将歌曲COPY到卡中，而我电脑中的歌曲我一般都修改了属性，将备注里面的歌手名什么的写的很详细（有些歌曲下下来就很详细），然后这些歌在手机上播放就是乱码。我重新在电脑里面把属性里面的备注全部删掉，重新将歌曲COPY到卡中，呵呵，解决了，歌曲名也显示为中文了。', N'2005-10-22 00:00:00', NULL)
