INSERT INTO [dbo].[sys_wap_book] ([id], [userid], [username], [book_classid], [book_title], [book_author], [book_pub], [book_content], [book_date], [HangBiaoShi]) VALUES (N'3502', N'69', N'3gair', N'6', N'10种可消耗胆固醇的食物', N'不详', N'不详', N'因为体内胆固醇堆积过多而引发各种疾病的现代人为数不少。但是如何才能够帮助自己“消耗”掉多余的胆固醇呢？ 　　现在来看看美国专家给我们提供的这份健康食单。已经患高胆固醇血症的人要常吃以下10种食物： 　　苹果、胡萝卜、玉米、牡蛎、杏仁、海带、大蒜、牛奶、蜜橘、茶。', N'2006-09-22 00:00:00', NULL)
