INSERT INTO [dbo].[sys_wap_book] ([id], [userid], [username], [book_classid], [book_title], [book_author], [book_pub], [book_content], [book_date], [HangBiaoShi]) VALUES (N'3534', NULL, NULL, N'97', N'关于手机“呼叫转移”功能实现的基本原理', N'不详', N'不详', N'全是网络实现的，而绝非手机。通过手机菜单做一次转移，手机只是自动在你需要转移的号码前加上转移代码并发送到网络，由网络返回确认信息，即完成一次转移设定。这和你直接用键盘输入转移代码+转移电话号码的效果是完全一样。许多朋友就是直接在键盘输入转移码+要转移的号码实现转移的，这样做的好处是快。但我习惯了借助手机的菜单辅助功能，没记转移码。简单比喻，手机完成的工作只相当与你拨一个长途电话，手机自动帮你在号码前面加上一个区号而已，能否拨出长途，只看你的网络及交换机设置，和你的电话本身无任何关系。转移设定后，有关转移的数据也只存放在sim卡，手机上没有任何相关数据记录。如果转移有问题，那么就是你的网络出了问题！', N'2005-11-05 00:00:00', NULL)
GO

INSERT INTO [dbo].[sys_wap_book] ([id], [userid], [username], [book_classid], [book_title], [book_author], [book_pub], [book_content], [book_date], [HangBiaoShi]) VALUES (N'3535', N'69', N'3gair', N'21', N'修理排线方法', N'不详', N'不详', N'告诉你一个方法修理排线方法一般故障多为排线和主板接触不良,打开后该盖，下掉电池，旋下天线，拔下发光灯罩，用一个扁口小起子，将后盖的四个扣子小心拨开，这样就下开了手机，从槽口中起出排线，在排线接头处，涂上少许焊锡膏，用一个小尖电烙铁，依次镀上一层薄锡，完毕后,用酒精反复清洗排线接头，洗掉焊锡膏（为啥？不必多说），重新安上排线接头，按下的方法装配好,则大功告成不过根据motorola维修记录统计，对于行货手机，除非是人为拆过机器，一般情况出现的屏幕显示、背景灯不亮、耳机无声的问题都是排线折断引起。翻修板极就常因多次拆装以及装配的问题，导致排线卡口接线焊接松动、卡接不良的情况。', N'2006-09-22 00:00:00', NULL)
GO

INSERT INTO [dbo].[sys_wap_book] ([id], [userid], [username], [book_classid], [book_title], [book_author], [book_pub], [book_content], [book_date], [HangBiaoShi]) VALUES (N'3536', NULL, NULL, N'97', N'moto的v，a，t系列之我见', N'不详', N'不详', N'首先3中不同系列的手机在样式上有些不同，t系列的手机不是翻盖类型的，而v和a系列的是翻盖的，另外，3者在价格上有一定的差距，v系列的（v998，v8088，v66，v60，v70，v680，v730，v8060）价格要比t系列（t189，t190，t191）的价格要贵400到3000不等！t系列的手机比较适合现在还没有资金来源的学生朋友使用！v系列的手机比较的时尚，每一个v系列的手机刚出来的时候，都能引起大家很大的购买欲望，是时尚的代表！a系列（a6188，a6288）的主要是pda类型的，价格都是在2400左右，这类手机比较的适合商务人士使用！', N'2005-11-05 00:00:00', NULL)
GO

INSERT INTO [dbo].[sys_wap_book] ([id], [userid], [username], [book_classid], [book_title], [book_author], [book_pub], [book_content], [book_date], [HangBiaoShi]) VALUES (N'3537', NULL, NULL, N'97', N'关于短消息收到较晚的原因！！', N'不详', N'不详', N'1)如果你8点所在一个位置，该位置信号很不好，你接收到的bcc信号太弱，网络可能会认为你不在服务区而把短信存贮在服务器而不给予发送，10点左右移动到另外一个位置（一般是另一个小区范围），系统发现你进入服务区，于是把存储的信息发送给你。2）网络数据阻塞，这个好理解。一般是流量阻塞引起。3）手机问题，网络正常，但手机本身的接收或发射的信号电平低或频偏超标，同样会导致bcc信号找不到手机，认为手机不在服务区，而导致短信延迟！', N'2005-11-05 00:00:00', NULL)
GO

INSERT INTO [dbo].[sys_wap_book] ([id], [userid], [username], [book_classid], [book_title], [book_author], [book_pub], [book_content], [book_date], [HangBiaoShi]) VALUES (N'3538', NULL, NULL, N'97', N'解决moto无法输入#　*　等符号的答案', N'不详', N'不详', N'经过偶然一次操作发现　*　#键因为是左移和右移而无法输入，但在itap输入法下连续按1会比其他输入法下多出一些字符，那就包括*和#迅速告之大家，以免给各位好友谈恋爱带来不便……呵呵！！！', N'2005-11-05 00:00:00', NULL)
