INSERT INTO [dbo].[sys_wap_book] ([id], [userid], [username], [book_classid], [book_title], [book_author], [book_pub], [book_content], [book_date], [HangBiaoShi]) VALUES (N'3558', N'69', N'3gair', N'7', N'诺机短信加密', N'不详', N'不详', N'大家都知道，诺机是没有短信加密功能的，近日我发现一方法可实现间接加密，现提供给大家分享和讨论。方法是：选择你要加密的短信》操作》选复制到日历》选择日期》保存。这样，由于日历上的日期很多，不知道哪个日子是看不到信息的。实现了另类加密！！！自己要查看时，只要进入日历，输入自己设置的时间就能看到了，并且同样能进行修改编辑等操作！（要是大觉得还可以，请顶一下，谢谢！）', N'2006-09-22 00:00:00', NULL)
