INSERT INTO [dbo].[wap_novel] ([id], [siteid], [book_classid], [book_title], [book_author], [book_img], [book_info], [CharNum], [Protagonists], [Process], [Nature], [Authorized], [book_click], [book_re], [SectionsNum], [VolumeNum], [MonthClick], [WeekClick], [DayClick], [FlowerClick], [EggClick], [FirstIssue], [CollectionNum], [RecommendNum], [Star], [LastClickTime], [LastUpdateTime], [LastUpdateBook], [Oper_userid], [Oper_time], [ischeck], [HangBiaoShi]) VALUES (N'1', N'106', N'106', N'宫杀：凤帷春醉（全本）', N'水凝烟', N'', N'身为丫鬟替嫁入宫，本想安身立命寻求清静，怎奈后宫从来就不是如履薄冰便可自保之地，本想站在局外看她人勾心斗角，怎成想自己早已便是局中人。[br]她说，我本想只做后宫一树清雅梨花，是你们将我逼上这权势巅峰……[br]既然只有帝王宠爱才能周全自身，争来又何妨？那看似温情的眼神，和宠极一时的荣华，在自己陷入深情后，才渐渐看清一切是假……原来，帝王爱不得。[br][br]满地尸骨也罢，杀罚决戮也罢，都只是步步为营的心机。[br][br]金冠霞衣站在高处，她是那么的令人尊崇光荣，然而对着那满地清魂，梨花如雪，她辨不清到底是谁才是真正的赢家，亦，无人知道红颜背后的痛和——惶恐……[br][br]白骨血腥中，她分明记得，那日朝霞初升，香风拂吹，他曾经笑挽着她的手指天而誓，“在天愿做比翼鸟，在地愿为连理枝……！”[br][br]只以为从此天荒地老，却原来，此恨绵绵无绝期！[br]***********[br][br]在此推荐烟的全本文：[br][br]（宫斗）树欲静而风不止，在宫里众妃对她诸多迫.害后。她终于明白，进了宫，原来，真的就由不得自己了。 《后宫虐杀：落尽梨花春又了》 http://novel.hongxiu.com/a/46170/[br][br]（爱恨情仇）《后宫虐杀》续篇，为弥补《后宫虐杀》一文之遗憾而写，太子妃大婚当日被劫，劫持他的男子却是自己的救命恩人，更是太子的亲哥哥，他将她碎裂的嫁衣送去太子妃，却又在将她送回太子身边备受凌辱之即，发现自己爱上了她……《玉碎宫倾》http://novel.hongxiu.com/a/76763/[br][br]（情斗加宫斗加权斗）她替姐姐出嫁，三日回门，姐姐却发现新郎是自己痴心要找的人，误会，诬蔑，他爱她，却不得不嫉恨，她爱他，却越爱越伤，而她的泪眼竟揉进了他弟弟的胸膛，兄弟二人的爱太过沉重，她到底何去何从？《替嫁：衾香薄》http://novel.hongxiu.com/a/253035/[br][br]（家斗.复仇）家人被一夜屠杀，她脱离清净的佛门以身复仇，嫁给仇人后却发现自己成了心上人的五娘，强忍情字煎熬的她步步为营，却在如愿以偿的刹那，知道了自己错得那样离谱。《妻妾斗：妻不如妾》http://novel.hongxiu.com/a/120228/[br][br]（皆大欢喜）她抱着发一笔的念头潜进即将嫁女的尚书府，却技不如人被这家的小姐替着替嫁上了花轿，知道不会有好果子吃的她一路逃啊逃，却不知，自己最亲密的大哥，正是自己千方百计要逃避的人！《逼上花轿的贼》http://novel.hongxiu.com/a/34768/', N'0', N'', N'连载中', N'公共作品', N'转载作品', N'0', N'0', N'0', N'0', N'0', N'0', N'0', N'0', N'0', N'0', N'0', N'0', N'★★★★★', N'2012-07-10 10:51:00', N'2012-07-10 10:50:44.077', NULL, N'106', N'2012-07-10 10:51:00', N'0', N'0')
