 <script type="text/javascript">
    //设置元素透明度,透明度值按IE规则计,即0~100
     function SetOpacity(ev, v){

       ev.filters ? ev.style.filter = ''alpha(opacity='' + v + '')'' : ev.style.opacity = v / 100;
    }
    /*
     * 淡入效果
     * elem==>需要淡入的元素
     * speed==>淡入速度,正整数(可选)
     * opacity==>淡入到指定的透明度,0~100(可选)
     */
    function fadeIn(elem, speed, opacity){
        speed = speed || 20;
        opacity = opacity || 100;
        //显示元素,并将元素值为0透明度(不可见)
        elem.style.display = ''block'';
        SetOpacity(elem, 0);
        //初始化透明度变化值为0
        var val = 0;
        //循环将透明值以5递增,即淡入效果
        (function(){
            SetOpacity(elem, val);
            val += 5;
            if (val <= opacity) {
                setTimeout(arguments.callee, speed)
            }
        })();
    }

    /*
     * 淡出效果
     * elem==>需要淡入的元素
     * speed==>淡入速度,正整数(可选)
     * opacity==>淡入到指定的透明度,0~100(可选)
     */
    function fadeOut(elem, speed, opacity){
       
        speed = speed || 20;
        opacity = opacity || 0;
        //初始化透明度变化值为0
        var val = 100;
        //循环将透明值以5递减,即淡出效果
        (function(){
            SetOpacity(elem, val);
            val -= 5;
            if (val >= opacity) {
                setTimeout(arguments.callee, speed);
            }else if (val < 0) {
                //元素透明度为0后隐藏元素
                elem.style.display = ''none'';
            }
        })();
    }

       
    //-->
    </script>', N'1', N'0000FF', N'0', N'126', N'2014-07-18 10:31:23.433', N'0', NULL)
