-- View structure for ClassSystypeView
-- ----------------------------
IF EXISTS (SELECT * FROM sys.all_objects WHERE object_id = OBJECT_ID(N'[dbo].[ClassSystypeView]') AND type IN ('V'))
	DROP VIEW [dbo].[ClassSystypeView]
GO

CREATE VIEW [dbo].[ClassSystypeView] AS SELECT dbo.class.classid, dbo.class.childid, dbo.class.userid, dbo.class.classname, dbo.class.typeid, 
    dbo.class.[position], dbo.class.smallimg, dbo.class.siteimg, dbo.class.sitelist, dbo.class.siterowremark, 
    dbo.class.sitedowntip, dbo.class.hits, dbo.class.articlenum, dbo.class.adminusername, dbo.class.introduce, 
    dbo.class.rank, dbo.class.creatdate, dbo.class.ismodel, dbo.class.ishidden, dbo.class.password, 
    dbo.class.needMoney, dbo.class.subMoney, dbo.class.allowUser, dbo.class.topicID, dbo.class.bbsFace, 
    dbo.class.bbsType, dbo.systype.typename, dbo.systype.typepath, dbo.systype.type, dbo.systype.remark, 
    dbo.class.HangBiaoShi, dbo.class.total
FROM dbo.class INNER JOIN
    dbo.systype ON dbo.class.typeid = dbo.systype.typeid
GO


-- ----------------------------
-- View structure for systype_user_view
-- ----------------------------
IF EXISTS (SELECT * FROM sys.all_objects WHERE object_id = OBJECT_ID(N'[dbo].[systype_user_view]') AND type IN ('V'))
	DROP VIEW [dbo].[systype_user_view]
GO

CREATE VIEW [dbo].[systype_user_view] AS SELECT     a.typeid, a.typename, a.typepath, a.typepath + 'x' AS nettypepath, a.type, a.remark, a.createuserid, a.createdate, a.rank, a.HangBiaoShi, b.username, 
                      b.managerlvl
FROM         dbo.systype AS a LEFT OUTER JOIN
                      dbo.[user] AS b ON a.createuserid = b.userid
GO


-- ----------------------------
-- View structure for UserView
-- ----------------------------
IF EXISTS (SELECT * FROM sys.all_objects WHERE object_id = OBJECT_ID(N'[dbo].[UserView]') AND type IN ('V'))
	DROP VIEW [dbo].[UserView]
GO

CREATE VIEW [dbo].[UserView] AS SELECT dbo.[user].userid, dbo.[user].HangBiaoShi, dbo.[user].siteid, dbo.[user].username, dbo.[user].nickname, 
    dbo.[user].password, dbo.[user].managerlvl, dbo.[user].sex, dbo.[user].age, dbo.[user].shenggao, 
    dbo.[user].tizhong, dbo.[user].xingzuo, dbo.[user].aihao, dbo.[user].fenfuo, dbo.[user].zhiye, dbo.[user].city, 
    dbo.[user].mobile, dbo.[user].email, dbo.[user].money, dbo.[user].moneyname, dbo.[user].moneyregular, 
    dbo.[user].RegTime, dbo.[user].LastLoginIP, dbo.[user].LastLoginTime, dbo.[user].LoginTimes, 
    dbo.[user].LockUser, dbo.[user].headimg, dbo.[user].remark, dbo.[user].sitename, dbo.[user].siteimg, 
    dbo.[user].siteuptip, dbo.[user].sitedowntip, dbo.[user].siteposition, dbo.[user].siterowremark, 
    dbo.[user].sitelistflag, dbo.[user].sitelist, dbo.[user].sitetype, dbo.[user].MaxPerPage_Default, 
    dbo.[user].MaxPerPage_Content, dbo.[user].MaxFileSize, dbo.[user].SaveUpFilesPath, 
    dbo.[user].UpFileType, dbo.[user].CharFilter, dbo.[user].UAFilter, dbo.[user].SessionTimeout, 
    dbo.[user].MailServer, dbo.[user].MailServerUserName, dbo.[user].MailServerPassWord, 
    dbo.[user].sitemoneyname, dbo.[user].sitespace, dbo.[user].myspace, dbo.[user].siteRight, 
    dbo.[user].isCheck, dbo.[user].endTime, dbo.[user].SidTimeOut, dbo.[user].lvlNumer, dbo.[user].lvlTimeImg, 
    dbo.[user].lvlRegular, dbo.[user].myBankMoney, dbo.[user].myBankTime, dbo.[user].chuiNiu, dbo.[user].rmb, 
    dbo.[user].expr, dbo.[user].sitevip, dbo.[user].managerlvl AS Roles, 
    CASE dbo.[user].sex WHEN 1 THEN '男性' WHEN 0 THEN '女性' END AS Gender, 
    CASE dbo.[user].LockUser WHEN 0 THEN '正常' WHEN 1 THEN '锁定' END AS State, DATEDIFF(dd, 
    GETDATE(), dbo.[user].endTime) AS pass_date, 
    CASE dbo.[user].ischeck WHEN 1 THEN '需要' ELSE '不要' END AS ischecks, 
    dbo.systype_siteid.systype
FROM dbo.[user] LEFT OUTER JOIN
    dbo.systype_siteid ON dbo.[user].userid = dbo.systype_siteid.siteid
GO


