    dbo.[user].nickname, dbo.[user].password, dbo.[user].managerlvl, dbo.[user].sex, 
    dbo.[user].age, dbo.[user].shenggao, dbo.[user].tizhong, dbo.[user].xingzuo, 
    dbo.[user].aihao, dbo.[user].fenfuo, dbo.[user].zhiye, dbo.[user].city, dbo.[user].mobile, 
    dbo.[user].email, dbo.[user].money, dbo.[user].moneyname, dbo.[user].moneyregular, 
    dbo.[user].RegTime, dbo.[user].LastLoginIP, dbo.[user].LastLoginTime, 
    dbo.[user].LoginTimes, dbo.[user].LockUser, dbo.[user].headimg, dbo.[user].remark, 
    dbo.[user].MaxPerPage_Content, dbo.[user].SessionTimeout, 
    dbo.[user].MailServerUserName, dbo.[user].isCheck, dbo.[user].endTime, 
    dbo.[user].SidTimeOut, dbo.[user].myBankMoney, dbo.[user].myBankTime, 
    dbo.[user].chuiNiu, dbo.[user].expR, dbo.[user].RMB, dbo.[user].ZoneCount, DATEDIFF(dd, 
    dbo.[user].myBankTime, GETDATE()) AS dtimes, CASE WHEN EXISTS
        (SELECT TOP 1 userid
      FROM [fcount]
      WHERE dbo.[fcount].userid = dbo.[USER].userid) THEN '1' ELSE '0' END AS isonline, 
    ISNULL(dbo.wap2_smallType.subclassName, '') AS idname, dbo.[user].bbsCount, 
    dbo.[user].bbsReCount, dbo.[user].actionTime, dbo.[user].actionState, 
    dbo.[user].TJcount
FROM dbo.[user] LEFT OUTER JOIN
    dbo.wap2_smallType ON dbo.[user].SessionTimeout = dbo.wap2_smallType.id
