	DROP VIEW [dbo].[wap_airplane_view]
GO

CREATE VIEW [dbo].[wap_airplane_view] AS SELECT a.id, a.siteid, a.classid, a.planeltd, a.smallimg, a.planeNum, a.planeType, a.seatType, a.money, 
    a.startCity, a.endCity, a.startTime, a.endTime, a.istop, a.isgood, a.hits, a.addtime, a.smalltype, 
    ISNULL(a.ischeck, 1) AS ischeck, a.HangBiaoShi, ISNULL(b.classname, 
