CREATE PROCEDURE [dbo].[UP_GetRecordByPageOrder]
@tblName varchar(255),   -- 表名 
@fldName varchar(255),   -- 显示字段名 
@OrderfldName varchar(255), -- 排序字段名 
@StatfldName varchar(255), -- 统计字段名 
@TotalCount int,      -- 总记录数
@PageSize int = 10,   -- 页尺寸 
@PageIndex int = 1,   -- 页码 
@IsReCount bit = 0,   -- 返回记录总数, 非 0 值则返回 
@OrderType bit = 0,   -- 设置排序类型, 非 0 值则降序 
@strWhere varchar(1000) = '' -- 查询条件 (注意: 不要加 where) 
AS begin
declare @strSql nvarchar(4000);	-- 执行语句
declare @strOrderType varchar(4); -- 排序类型
declare @strDOrderType varchar(4); -- 反排序类型
declare @strOrderString varchar (100); -- 排序语句
declare @strDOrderString varchar (100); -- 反排序语句
if @fldName = ''
	set @fldName = '*';
--
if @OrderType = 0
	begin
		set @strOrderType = 'ASC';
		set @strDOrderType = 'DESC';
	end
else
	begin
		set @strOrderType = 'DESC'
		set @strDOrderType = 'ASC'
	end
-- 组合 Order by 语句。之所以加一个HangBiaoShi，是因为如果排序字段都一样，就得不到想要的排序结果
if @OrderfldName != '' and lower(@OrderfldName) != 
'hangbiaoshi'
	begin
		set @strOrderString = ' order by ' + 
@OrderfldName + ' ' + @strOrderType
			+ ',hangbiaoshi asc';
		set @strDOrderString = ' order by ' + 
@OrderfldName + ' ' + @strDOrderType
			+ ',hangbiaoshi desc';
	end
else if @OrderfldName != '' and lower(@OrderfldName) = 
'hangbiaoshi'
	begin
		set @strOrderString = ' order by ' + 
@OrderfldName + ' ' + @strOrderType;
		set @strDOrderString = ' order by ' + 
@OrderfldName + ' ' + @strDOrderType;
	end
else if @OrderfldName = ''
	begin
		set @strOrderString = ' order by 
hangbiaoshi asc';
		set @strDOrderString = ' order by 
hangbiaoshi desc';
	end
--获取实际需要返回的记录数
declare @ReturnCount int;
set @ReturnCount = @PageSize;
if @PageSize * @PageIndex - @TotalCount > 0
	set @ReturnCount = @PageSize - (@PageSize * 
@PageIndex - @TotalCount);
-- 组合查询语句
set @strSql = 'select * from 
(select top ' + Convert(varchar(100),@ReturnCount) + ' * 
from 
(select top ' + Convert(varchar(100),@PageSize * 
@PageIndex) + @fldName + ' from ' + @tblName;
if @strWhere != ''
	set @strSql = @strSql + ' where ' + @strWhere;
set @strSql = @strSql + ' ' + @strOrderString + ') as ' 
+ @tblName
	+ ' ' + @strDOrderString + ') as ' + @tblName
	+ ' ' + @strOrderString;
-- 
exec (@strSql);
end
GO


